contracts/BLOCKSStakingV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title BLOCKSStakingV2
 * @dev Advanced staking contract for BlockCoop V2 ecosystem
 * @notice Allows users to stake BLOCKS tokens and earn USDT rewards
 *         Features multiple pools with different lock periods and APYs
 *         Integrates with BlockCoop V2 modular architecture
 */
contract BLOCKSStakingV2 is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Roles
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");
    bytes32 public constant REWARD_DISTRIBUTOR_ROLE = keccak256("REWARD_DISTRIBUTOR_ROLE");
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    // Precision for calculations
    uint256 private constant PRECISION = 1e18;
    uint256 private constant BASIS_POINTS = 10000;

    // Tokens
    IERC20 public immutable stakingToken;  // BLOCKS token
    IERC20 public immutable rewardToken;   // USDT token

    // Global staking statistics
    uint256 public totalStaked;
    uint256 public totalRewardsDistributed;
    uint256 public globalRewardRate; // Rewards per second per token staked

    // Staking pool structure
    struct StakingPool {
        string name;
        uint256 lockPeriod;        // Lock period in seconds
        uint256 apyBasisPoints;    // APY in basis points (e.g., 1200 = 12%)
        uint256 minStake;          // Minimum stake amount
        uint256 maxStake;          // Maximum stake amount per user
        uint256 totalStaked;       // Total amount staked in this pool
        uint256 rewardMultiplier;  // Multiplier for rewards (basis points)
        bool isActive;             // Whether the pool is active
        uint256 emergencyExitPenalty; // Penalty for emergency exit (basis points)
    }

    // User stake structure
    struct UserStake {
        uint256 amount;            // Amount staked
        uint256 stakedAt;          // Timestamp when staked
        uint256 lockEndTime;       // When the lock period ends
        uint256 lastRewardClaim;   // Last time rewards were claimed
        uint256 accumulatedRewards; // Accumulated unclaimed rewards
        bool isActive;             // Whether the stake is active
    }

    // Storage
    StakingPool[] public stakingPools;
    mapping(uint256 => mapping(address => UserStake)) public userStakes; // poolId => user => stake
    mapping(address => uint256[]) public userPoolIds; // user => array of pool IDs they've staked in
    mapping(address => uint256) public totalUserStaked; // user => total amount staked across all pools

    // Reward tracking
    mapping(uint256 => uint256) public poolRewardPerTokenStored; // poolId => reward per token
    mapping(uint256 => uint256) public poolLastUpdateTime; // poolId => last update time
    mapping(uint256 => mapping(address => uint256)) public userRewardPerTokenPaid; // poolId => user => paid amount

    // Events
    event PoolCreated(uint256 indexed poolId, string name, uint256 lockPeriod, uint256 apyBasisPoints);
    event PoolUpdated(uint256 indexed poolId, uint256 apyBasisPoints, bool isActive);
    event Staked(address indexed user, uint256 indexed poolId, uint256 amount, uint256 lockEndTime);
    event Unstaked(address indexed user, uint256 indexed poolId, uint256 amount, uint256 penalty);
    event RewardsClaimed(address indexed user, uint256 indexed poolId, uint256 amount);
    event RewardsDistributed(uint256 amount, uint256 newRewardRate);
    event EmergencyUnstake(address indexed user, uint256 indexed poolId, uint256 amount, uint256 penalty);

    constructor(
        address _stakingToken,
        address _rewardToken,
        address admin
    ) {
        require(_stakingToken != address(0), "BLOCKSStakingV2: Invalid staking token");
        require(_rewardToken != address(0), "BLOCKSStakingV2: Invalid reward token");
        require(admin != address(0), "BLOCKSStakingV2: Invalid admin");

        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(POOL_MANAGER_ROLE, admin);
        _grantRole(REWARD_DISTRIBUTOR_ROLE, admin);
        _grantRole(EMERGENCY_ROLE, admin);

        // Create default staking pools
        _createDefaultPools();
    }

    /**
     * @dev Create default staking pools
     */
    function _createDefaultPools() private {
        // Flexible Pool (0% lock, 8% APY)
        stakingPools.push(StakingPool({
            name: "Flexible Staking",
            lockPeriod: 0,
            apyBasisPoints: 800,  // 8%
            minStake: 1e18,       // 1 BLOCKS
            maxStake: 1000000e18, // 1M BLOCKS
            totalStaked: 0,
            rewardMultiplier: 10000, // 100% (no bonus)
            isActive: true,
            emergencyExitPenalty: 0 // No penalty for flexible
        }));

        // 30-Day Lock Pool (12% APY)
        stakingPools.push(StakingPool({
            name: "30-Day Lock",
            lockPeriod: 30 days,
            apyBasisPoints: 1200, // 12%
            minStake: 10e18,      // 10 BLOCKS
            maxStake: 1000000e18, // 1M BLOCKS
            totalStaked: 0,
            rewardMultiplier: 11500, // 115% (15% bonus)
            isActive: true,
            emergencyExitPenalty: 500 // 5% penalty
        }));

        // 90-Day Lock Pool (18% APY)
        stakingPools.push(StakingPool({
            name: "90-Day Lock",
            lockPeriod: 90 days,
            apyBasisPoints: 1800, // 18%
            minStake: 50e18,      // 50 BLOCKS
            maxStake: 1000000e18, // 1M BLOCKS
            totalStaked: 0,
            rewardMultiplier: 13000, // 130% (30% bonus)
            isActive: true,
            emergencyExitPenalty: 1000 // 10% penalty
        }));

        // 1-Year Lock Pool (25% APY)
        stakingPools.push(StakingPool({
            name: "1-Year Lock",
            lockPeriod: 365 days,
            apyBasisPoints: 2500, // 25%
            minStake: 100e18,     // 100 BLOCKS
            maxStake: 1000000e18, // 1M BLOCKS
            totalStaked: 0,
            rewardMultiplier: 15000, // 150% (50% bonus)
            isActive: true,
            emergencyExitPenalty: 2000 // 20% penalty
        }));

        emit PoolCreated(0, "Flexible Staking", 0, 800);
        emit PoolCreated(1, "30-Day Lock", 30 days, 1200);
        emit PoolCreated(2, "90-Day Lock", 90 days, 1800);
        emit PoolCreated(3, "1-Year Lock", 365 days, 2500);
    }

    /**
     * @dev Get the number of staking pools
     */
    function poolCount() external view returns (uint256) {
        return stakingPools.length;
    }

    /**
     * @dev Get user's active pool IDs
     */
    function getUserPoolIds(address user) external view returns (uint256[] memory) {
        return userPoolIds[user];
    }

    /**
     * @dev Check if user has an active stake in a pool
     */
    function hasActiveStake(address user, uint256 poolId) external view returns (bool) {
        return userStakes[poolId][user].isActive;
    }

    /**
     * @dev Get user's stake information for a specific pool
     */
    function getUserStake(address user, uint256 poolId) external view returns (
        uint256 amount,
        uint256 stakedAt,
        uint256 lockEndTime,
        uint256 pendingRewards,
        bool isLocked
    ) {
        UserStake memory stake = userStakes[poolId][user];
        amount = stake.amount;
        stakedAt = stake.stakedAt;
        lockEndTime = stake.lockEndTime;
        pendingRewards = _calculatePendingRewards(user, poolId);
        isLocked = block.timestamp < stake.lockEndTime;
    }

    /**
     * @dev Calculate pending rewards for a user in a specific pool
     */
    function _calculatePendingRewards(address user, uint256 poolId) private view returns (uint256) {
        UserStake memory stake = userStakes[poolId][user];
        if (!stake.isActive || stake.amount == 0) {
            return 0;
        }

        StakingPool memory pool = stakingPools[poolId];
        uint256 timeStaked = block.timestamp - stake.lastRewardClaim;

        // Calculate rewards based on APY and time staked
        uint256 annualReward = (stake.amount * pool.apyBasisPoints) / BASIS_POINTS;
        uint256 rewardForPeriod = (annualReward * timeStaked) / 365 days;

        // Apply pool multiplier
        rewardForPeriod = (rewardForPeriod * pool.rewardMultiplier) / BASIS_POINTS;

        return stake.accumulatedRewards + rewardForPeriod;
    }

    /**
     * @dev Get pending rewards for a user in a specific pool
     */
    function getPendingRewards(address user, uint256 poolId) external view returns (uint256) {
        return _calculatePendingRewards(user, poolId);
    }

    /**
     * @dev Stake tokens in a specific pool
     */
    function stake(uint256 poolId, uint256 amount) external whenNotPaused nonReentrant {
        require(poolId < stakingPools.length, "BLOCKSStakingV2: Invalid pool ID");
        require(amount > 0, "BLOCKSStakingV2: Amount must be greater than 0");

        StakingPool storage pool = stakingPools[poolId];
        require(pool.isActive, "BLOCKSStakingV2: Pool is not active");
        require(amount >= pool.minStake, "BLOCKSStakingV2: Amount below minimum stake");

        UserStake storage userStake = userStakes[poolId][msg.sender];
        require(userStake.amount + amount <= pool.maxStake, "BLOCKSStakingV2: Amount exceeds maximum stake");

        // If user already has a stake in this pool, claim pending rewards first
        if (userStake.isActive) {
            _claimRewards(msg.sender, poolId);
        } else {
            // Add pool to user's active pools
            userPoolIds[msg.sender].push(poolId);
        }

        // Transfer tokens from user to contract
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        // Update user stake
        userStake.amount += amount;
        userStake.stakedAt = block.timestamp;
        userStake.lockEndTime = block.timestamp + pool.lockPeriod;
        userStake.lastRewardClaim = block.timestamp;
        userStake.isActive = true;

        // Update pool and global statistics
        pool.totalStaked += amount;
        totalStaked += amount;
        totalUserStaked[msg.sender] += amount;

        emit Staked(msg.sender, poolId, amount, userStake.lockEndTime);
    }

    /**
     * @dev Unstake tokens from a specific pool
     */
    function unstake(uint256 poolId, uint256 amount) external whenNotPaused nonReentrant {
        require(poolId < stakingPools.length, "BLOCKSStakingV2: Invalid pool ID");
        require(amount > 0, "BLOCKSStakingV2: Amount must be greater than 0");

        UserStake storage userStake = userStakes[poolId][msg.sender];
        require(userStake.isActive, "BLOCKSStakingV2: No active stake");
        require(userStake.amount >= amount, "BLOCKSStakingV2: Insufficient staked amount");
        require(block.timestamp >= userStake.lockEndTime, "BLOCKSStakingV2: Stake is still locked");

        // Claim pending rewards first
        _claimRewards(msg.sender, poolId);

        // Update user stake
        userStake.amount -= amount;
        if (userStake.amount == 0) {
            userStake.isActive = false;
            _removePoolFromUser(msg.sender, poolId);
        }

        // Update pool and global statistics
        stakingPools[poolId].totalStaked -= amount;
        totalStaked -= amount;
        totalUserStaked[msg.sender] -= amount;

        // Transfer tokens back to user
        stakingToken.safeTransfer(msg.sender, amount);

        emit Unstaked(msg.sender, poolId, amount, 0);
    }

    /**
     * @dev Emergency unstake with penalty (for locked stakes)
     */
    function emergencyUnstake(uint256 poolId, uint256 amount) external whenNotPaused nonReentrant {
        require(poolId < stakingPools.length, "BLOCKSStakingV2: Invalid pool ID");
        require(amount > 0, "BLOCKSStakingV2: Amount must be greater than 0");

        UserStake storage userStake = userStakes[poolId][msg.sender];
        require(userStake.isActive, "BLOCKSStakingV2: No active stake");
        require(userStake.amount >= amount, "BLOCKSStakingV2: Insufficient staked amount");

        StakingPool storage pool = stakingPools[poolId];

        // Calculate penalty
        uint256 penalty = 0;
        if (block.timestamp < userStake.lockEndTime) {
            penalty = (amount * pool.emergencyExitPenalty) / BASIS_POINTS;
        }

        // Claim pending rewards first
        _claimRewards(msg.sender, poolId);

        // Update user stake
        userStake.amount -= amount;
        if (userStake.amount == 0) {
            userStake.isActive = false;
            _removePoolFromUser(msg.sender, poolId);
        }

        // Update pool and global statistics
        pool.totalStaked -= amount;
        totalStaked -= amount;
        totalUserStaked[msg.sender] -= amount;

        // Transfer tokens back to user (minus penalty)
        uint256 amountToReturn = amount - penalty;
        if (amountToReturn > 0) {
            stakingToken.safeTransfer(msg.sender, amountToReturn);
        }

        // Send penalty to treasury (burn or send to admin)
        if (penalty > 0) {
            // For now, keep penalty in contract as additional rewards
            // Could be modified to send to treasury address
        }

        emit EmergencyUnstake(msg.sender, poolId, amount, penalty);
    }

    /**
     * @dev Claim rewards from a specific pool
     */
    function claimRewards(uint256 poolId) external whenNotPaused nonReentrant {
        require(poolId < stakingPools.length, "BLOCKSStakingV2: Invalid pool ID");
        _claimRewards(msg.sender, poolId);
    }

    /**
     * @dev Internal function to claim rewards
     */
    function _claimRewards(address user, uint256 poolId) private {
        uint256 pendingRewards = _calculatePendingRewards(user, poolId);
        if (pendingRewards == 0) {
            return;
        }

        UserStake storage userStake = userStakes[poolId][user];
        userStake.lastRewardClaim = block.timestamp;
        userStake.accumulatedRewards = 0;

        // Transfer rewards to user
        rewardToken.safeTransfer(user, pendingRewards);
        totalRewardsDistributed += pendingRewards;

        emit RewardsClaimed(user, poolId, pendingRewards);
    }

    /**
     * @dev Remove pool from user's active pools array
     */
    function _removePoolFromUser(address user, uint256 poolId) private {
        uint256[] storage pools = userPoolIds[user];
        for (uint256 i = 0; i < pools.length; i++) {
            if (pools[i] == poolId) {
                pools[i] = pools[pools.length - 1];
                pools.pop();
                break;
            }
        }
    }

    /**
     * @dev Claim rewards from all active pools
     */
    function claimAllRewards() external whenNotPaused nonReentrant {
        uint256[] memory pools = userPoolIds[msg.sender];
        for (uint256 i = 0; i < pools.length; i++) {
            if (userStakes[pools[i]][msg.sender].isActive) {
                _claimRewards(msg.sender, pools[i]);
            }
        }
    }

    /**
     * @dev Get total pending rewards across all pools for a user
     */
    function getTotalPendingRewards(address user) external view returns (uint256) {
        uint256 totalPending = 0;
        uint256[] memory pools = userPoolIds[user];
        for (uint256 i = 0; i < pools.length; i++) {
            if (userStakes[pools[i]][user].isActive) {
                totalPending += _calculatePendingRewards(user, pools[i]);
            }
        }
        return totalPending;
    }

    /**
     * @dev Create a new staking pool (admin only)
     */
    function createPool(
        string memory name,
        uint256 lockPeriod,
        uint256 apyBasisPoints,
        uint256 minStake,
        uint256 maxStake,
        uint256 rewardMultiplier,
        uint256 emergencyExitPenalty
    ) external onlyRole(POOL_MANAGER_ROLE) {
        require(bytes(name).length > 0, "BLOCKSStakingV2: Pool name cannot be empty");
        require(apyBasisPoints <= 10000, "BLOCKSStakingV2: APY cannot exceed 100%");
        require(minStake > 0, "BLOCKSStakingV2: Minimum stake must be greater than 0");
        require(maxStake >= minStake, "BLOCKSStakingV2: Maximum stake must be >= minimum stake");
        require(rewardMultiplier >= BASIS_POINTS, "BLOCKSStakingV2: Reward multiplier must be >= 100%");
        require(emergencyExitPenalty <= 5000, "BLOCKSStakingV2: Emergency exit penalty cannot exceed 50%");

        stakingPools.push(StakingPool({
            name: name,
            lockPeriod: lockPeriod,
            apyBasisPoints: apyBasisPoints,
            minStake: minStake,
            maxStake: maxStake,
            totalStaked: 0,
            rewardMultiplier: rewardMultiplier,
            isActive: true,
            emergencyExitPenalty: emergencyExitPenalty
        }));

        uint256 poolId = stakingPools.length - 1;
        emit PoolCreated(poolId, name, lockPeriod, apyBasisPoints);
    }

    /**
     * @dev Update pool parameters (admin only)
     */
    function updatePool(
        uint256 poolId,
        uint256 apyBasisPoints,
        uint256 rewardMultiplier,
        bool isActive
    ) external onlyRole(POOL_MANAGER_ROLE) {
        require(poolId < stakingPools.length, "BLOCKSStakingV2: Invalid pool ID");
        require(apyBasisPoints <= 10000, "BLOCKSStakingV2: APY cannot exceed 100%");
        require(rewardMultiplier >= BASIS_POINTS, "BLOCKSStakingV2: Reward multiplier must be >= 100%");

        StakingPool storage pool = stakingPools[poolId];
        pool.apyBasisPoints = apyBasisPoints;
        pool.rewardMultiplier = rewardMultiplier;
        pool.isActive = isActive;

        emit PoolUpdated(poolId, apyBasisPoints, isActive);
    }

    /**
     * @dev Distribute rewards to the contract (admin only)
     */
    function distributeRewards(uint256 amount) external onlyRole(REWARD_DISTRIBUTOR_ROLE) {
        require(amount > 0, "BLOCKSStakingV2: Amount must be greater than 0");

        // Transfer reward tokens to this contract
        rewardToken.safeTransferFrom(msg.sender, address(this), amount);

        emit RewardsDistributed(amount, globalRewardRate);
    }

    /**
     * @dev Emergency withdraw function (admin only)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyRole(EMERGENCY_ROLE) {
        require(token != address(stakingToken) || amount <= IERC20(stakingToken).balanceOf(address(this)) - totalStaked,
                "BLOCKSStakingV2: Cannot withdraw staked tokens");

        IERC20(token).safeTransfer(msg.sender, amount);
    }

    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Get contract statistics
     */
    function getContractStats() external view returns (
        uint256 _totalStaked,
        uint256 _totalRewardsDistributed,
        uint256 _poolCount,
        uint256 _rewardTokenBalance
    ) {
        _totalStaked = totalStaked;
        _totalRewardsDistributed = totalRewardsDistributed;
        _poolCount = stakingPools.length;
        _rewardTokenBalance = rewardToken.balanceOf(address(this));
    }
}

contracts/DividendDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title DividendDistributor
 * @dev Distributes dividends to BLOCKS token holders proportionally to their holdings
 * @notice This contract allows for dividend distribution in any ERC20 token (typically USDT)
 *         to BLOCKS token holders based on their proportional ownership
 */
contract DividendDistributor is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    bytes32 public constant DISTRIBUTOR_ROLE = keccak256("DISTRIBUTOR_ROLE");

    // The BLOCKS token contract
    IERC20 public immutable token;

    // The dividend token (typically USDT)
    IERC20 public immutable dividendToken;

    // Total dividends distributed
    uint256 public totalDividendsDistributed;

    // Dividend tracking per user
    mapping(address => uint256) private _dividendsWithdrawn;
    mapping(address => uint256) private _lastDividendPoints;

    // Global dividend tracking
    uint256 private _totalDividendPoints;
    uint256 private constant PRECISION = 1e18;

    // Events
    event DividendDistributed(uint256 amount, uint256 totalSupply);
    event DividendClaimed(address indexed user, uint256 amount);

    /**
     * @dev Constructor
     * @param _blocksToken Address of the BLOCKS token contract
     * @param _dividendToken Address of the dividend token contract (typically USDT)
     * @param admin Address of the admin
     */
    constructor(
        address _blocksToken,
        address _dividendToken,
        address admin
    ) {
        require(_blocksToken != address(0), "DividendDistributor: Invalid BLOCKS token address");
        require(_dividendToken != address(0), "DividendDistributor: Invalid dividend token address");
        require(admin != address(0), "DividendDistributor: Invalid admin address");

        token = IERC20(_blocksToken);
        dividendToken = IERC20(_dividendToken);

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(DISTRIBUTOR_ROLE, admin);
    }

    /**
     * @dev Distribute dividends to all BLOCKS token holders
     * @param amount Amount of dividend tokens to distribute
     */
    function distributeDividends(uint256 amount) external onlyRole(DISTRIBUTOR_ROLE) whenNotPaused nonReentrant {
        require(amount > 0, "DividendDistributor: Amount must be greater than 0");

        uint256 totalSupply = token.totalSupply();
        require(totalSupply > 0, "DividendDistributor: No tokens in circulation");

        // Transfer dividend tokens from sender to this contract
        dividendToken.safeTransferFrom(msg.sender, address(this), amount);

        // Update global dividend points
        _totalDividendPoints += (amount * PRECISION) / totalSupply;
        totalDividendsDistributed += amount;

        emit DividendDistributed(amount, totalSupply);
    }

    /**
     * @dev Claim pending dividends for the caller
     */
    function claimDividend() external whenNotPaused nonReentrant {
        uint256 pendingDividends = getPendingDividends(msg.sender);
        require(pendingDividends > 0, "DividendDistributor: No dividends to claim");

        // Update user's last dividend points
        _lastDividendPoints[msg.sender] = _totalDividendPoints;
        _dividendsWithdrawn[msg.sender] += pendingDividends;

        // Transfer dividends to user
        dividendToken.safeTransfer(msg.sender, pendingDividends);

        emit DividendClaimed(msg.sender, pendingDividends);
    }

    /**
     * @dev Get pending dividends for a user
     * @param user Address of the user
     * @return Pending dividend amount
     */
    function getPendingDividends(address user) public view returns (uint256) {
        uint256 userBalance = token.balanceOf(user);
        if (userBalance == 0) {
            return 0;
        }

        uint256 newDividendPoints = _totalDividendPoints - _lastDividendPoints[user];
        return (userBalance * newDividendPoints) / PRECISION;
    }

    /**
     * @dev Get total dividends withdrawn by a user
     * @param user Address of the user
     * @return Total dividends withdrawn
     */
    function getDividendsWithdrawn(address user) external view returns (uint256) {
        return _dividendsWithdrawn[user];
    }

    /**
     * @dev Get total dividends earned by a user (claimed + pending)
     * @param user Address of the user
     * @return Total dividends earned
     */
    function getTotalDividendsEarned(address user) external view returns (uint256) {
        return _dividendsWithdrawn[user] + getPendingDividends(user);
    }

    /**
     * @dev Get dividend distribution statistics
     * @return totalDistributed Total dividends distributed
     * @return totalClaimed Total dividends claimed by all users
     * @return totalPending Total pending dividends across all users
     */
    function getDividendStats() external view returns (
        uint256 totalDistributed,
        uint256 totalClaimed,
        uint256 totalPending
    ) {
        totalDistributed = totalDividendsDistributed;
        totalClaimed = totalDistributed - dividendToken.balanceOf(address(this));
        totalPending = dividendToken.balanceOf(address(this));
    }

    /**
     * @dev Emergency function to withdraw stuck tokens (admin only)
     * @param tokenAddress Address of the token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address tokenAddress, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(tokenAddress != address(dividendToken), "DividendDistributor: Cannot withdraw dividend tokens");
        IERC20(tokenAddress).safeTransfer(msg.sender, amount);
    }

    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Update user's dividend tracking when their token balance changes
     * @param user Address of the user
     * @notice This should be called by the BLOCKS token contract on transfers
     */
    function updateDividendTracker(address user) external {
        require(msg.sender == address(token), "DividendDistributor: Only BLOCKS token can call this");

        // Claim any pending dividends before balance change
        uint256 pendingDividends = getPendingDividends(user);
        if (pendingDividends > 0) {
            _lastDividendPoints[user] = _totalDividendPoints;
            _dividendsWithdrawn[user] += pendingDividends;
            dividendToken.safeTransfer(user, pendingDividends);
            emit DividendClaimed(user, pendingDividends);
        } else {
            // Just update the tracking point
            _lastDividendPoints[user] = _totalDividendPoints;
        }
    }
}

contracts/PackageManagerV2_1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IERC20Decimals is IERC20 {
    function decimals() external view returns (uint8);
}

interface IBLOCKS {
    function mint(address to, uint256 amount) external;
}

interface IBLOCKS_LP {
    function mint(address to, uint256 amount) external;
    function burn(address from, uint256 amount) external;
    function totalSupply() external view returns (uint256);
}

interface IVestingVault {
    function lock(address user, uint256 amount, uint64 cliff, uint64 duration) external;
    function claim() external;
}

interface IPancakeRouter {
    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);

    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

interface IPancakeFactory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IPancakePair {
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function totalSupply() external view returns (uint256);
}

interface ISwapTaxManager {
    function buckets(bytes32 key) external view returns (uint16 rateBps, address recipient);
}

/**
 * @title PackageManagerV2_1
 * @dev Manages investment packages with sophisticated token splitting, vesting, and liquidity provision
 * @notice This contract handles package creation, purchases with automatic token distribution,
 *         vesting schedules, liquidity provision, and referral rewards
 */
contract PackageManagerV2_1 is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20Decimals;

    bytes32 public constant ADMIN_ROLE = DEFAULT_ADMIN_ROLE;

    struct Package {
        string   name;
        uint256  entryUSDT;
        uint256  exchangeRate;     // Exchange rate: USDT per BLOCKS for user token allocation (e.g., 2000000 = 2.0 USDT per BLOCKS)
        uint16   vestBps;
        uint64   cliff;
        uint64   duration;
        uint16   referralBps;
        bool     active;
        bool     exists;  // Added to track package existence
    }

    // State variables
    IERC20Decimals   public immutable usdt;
    IBLOCKS          public immutable shareToken;
    IBLOCKS_LP       public immutable lpToken;
    IVestingVault    public immutable vestingVault;
    IPancakeRouter   public immutable router;
    IPancakeFactory  public immutable factory;
    ISwapTaxManager  public immutable taxManager;
    address          public           treasury;
    uint256          public           deadlineWindow = 300; // 5 minutes in seconds
    uint256          public           globalTargetPrice; // Global target price for liquidity operations only
    uint256          public           slippageTolerance = 500; // 5% slippage tolerance in basis points (500 = 5%)

    uint256   public nextPackageId;
    uint256[] private _packageIds;
    mapping(uint256 => Package) private _packages;

    // User portfolio tracking
    struct UserPurchase {
        uint256 packageId;
        uint256 usdtAmount;
        uint256 totalTokens;
        uint256 vestTokens;
        uint256 poolTokens;
        uint256 lpTokens;
        address referrer;
        uint256 referralReward;
        uint256 timestamp;
    }

    struct UserStats {
        uint256 totalInvested;        // Total USDT invested
        uint256 totalTokensReceived;  // Total tokens received (vest + pool)
        uint256 totalVestTokens;      // Total tokens in vesting
        uint256 totalPoolTokens;      // Total tokens used for liquidity
        uint256 totalLPTokens;        // Total LP tokens received
        uint256 totalReferralRewards; // Total referral rewards earned
        uint256 purchaseCount;        // Number of packages purchased
        uint256 redemptionCount;      // Number of redemptions made
        uint256 totalRedemptions;     // Total LP tokens redeemed
    }

    // User data mappings
    mapping(address => UserStats) private _userStats;
    mapping(address => UserPurchase[]) private _userPurchases;
    mapping(address => uint256[]) private _userRedemptions; // LP amounts redeemed
    mapping(address => uint256[]) private _userRedemptionTimestamps;

    // Constants for tax bucket keys
    bytes32 public constant PURCHASE_TAX_KEY = keccak256("PURCHASE");
    bytes32 public constant REFERRAL_TAX_KEY = keccak256("REFERRAL");

    // Events
    event PackageAdded(
        uint256 indexed id,
        string name,
        uint256 entryUSDT,
        uint256 exchangeRate,
        uint16 vestBps,
        uint64 cliff,
        uint64 duration,
        uint16 referralBps
    );

    event PackageToggled(uint256 indexed id, bool active);

    event PackageExchangeRateUpdated(
        uint256 indexed id,
        uint256 oldExchangeRate,
        uint256 newExchangeRate
    );

    event GlobalTargetPriceUpdated(
        uint256 oldGlobalTargetPrice,
        uint256 newGlobalTargetPrice
    );

    event Purchased(
        address indexed buyer,
        uint256 indexed packageId,
        uint256 usdtAmount,
        uint256 totalTokens,
        uint256 vestTokens,
        uint256 poolTokens,
        uint256 lpTokens,
        address indexed referrer,
        uint256 referralReward
    );

    event Redeemed(
        address indexed user,
        uint256 lpAmount
    );

    event LiquidityRedeemed(
        address indexed user,
        uint256 lpTokensBurned,
        uint256 liquidityRemoved,
        uint256 shareTokenReceived,
        uint256 usdtReceived
    );

    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event DeadlineWindowUpdated(uint256 oldWindow, uint256 newWindow);

    event TaxApplied(bytes32 indexed taxKey, uint256 amount, address indexed recipient);

    event ReferralPaid(
        address indexed referrer,
        address indexed buyer,
        uint256 reward
    );

    event TreasuryBlocksAllocated(
        address indexed buyer,
        uint256 indexed packageId,
        uint256 amount
    );

    // Enhanced liquidity addition events for improved transparency
    event LiquidityAdded(
        address indexed user,
        uint256 indexed packageId,
        uint256 shareTokenAmount,
        uint256 usdtAmount,
        uint256 liquidityTokens,
        uint256 actualShareToken,
        uint256 actualUSDT
    );

    event LiquidityAdditionFailed(
        address indexed user,
        uint256 indexed packageId,
        uint256 usdtAmount,
        string reason
    );

    event SlippageProtectionTriggered(
        address indexed user,
        uint256 indexed packageId,
        uint256 requestedShareToken,
        uint256 requestedUSDT,
        uint256 minShareToken,
        uint256 minUSDT
    );

    /**
     * @dev Constructor to initialize the PackageManager contract
     * @param usdt_ USDT token contract address (must use 18 decimals for V2 architecture)
     * @param share_ Share token contract address
     * @param lp_ LP token contract address
     * @param vault_ Vesting vault contract address
     * @param router_ PancakeSwap router contract address
     * @param factory_ PancakeSwap factory contract address
     * @param treasury_ Treasury address for receiving funds
     * @param tax_ Tax manager contract address
     * @param admin Admin address for access control
     * @param initialGlobalTargetPrice_ Initial global target price for liquidity operations (18 decimals)
     */
    constructor(
        address usdt_,
        address share_,
        address lp_,
        address vault_,
        address router_,
        address factory_,
        address treasury_,
        address tax_,
        address admin,
        uint256 initialGlobalTargetPrice_
    ) {
        require(usdt_ != address(0), "Invalid USDT address");
        require(share_ != address(0), "Invalid share token address");
        require(lp_ != address(0), "Invalid LP token address");
        require(vault_ != address(0), "Invalid vault address");
        require(router_ != address(0), "Invalid router address");
        require(factory_ != address(0), "Invalid factory address");
        require(treasury_ != address(0), "Invalid treasury address");
        require(tax_ != address(0), "Invalid tax manager address");
        require(admin != address(0), "Invalid admin address");
        require(initialGlobalTargetPrice_ > 0, "Invalid initial global target price");

        usdt         = IERC20Decimals(usdt_);
        shareToken   = IBLOCKS(share_);
        lpToken      = IBLOCKS_LP(lp_);
        vestingVault = IVestingVault(vault_);
        router       = IPancakeRouter(router_);
        factory      = IPancakeFactory(factory_);
        taxManager   = ISwapTaxManager(tax_);
        treasury     = treasury_;
        globalTargetPrice = initialGlobalTargetPrice_;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);

        // Grant admin role to additional admin wallet
        _grantRole(DEFAULT_ADMIN_ROLE, 0x6F6782148F208F9547f68e2354B1d7d2d4BeF987);
    }

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "PackageManager: Not admin");
        _;
    }

    modifier validPackage(uint256 id) {
        require(_packages[id].exists, "PackageManager: Invalid package");
        _;
    }

    /**
     * @dev Add a new investment package
     * @param name Package name
     * @param entryUSDT Entry amount in USDT (with 18 decimals for V2 architecture)
     * @param exchangeRate Exchange rate: USDT per BLOCKS for user token allocation (e.g., 2000000000000000000 = 2.0 USDT per BLOCKS with 18 decimals)
     * @param vestBps Percentage of tokens to vest in basis points
     * @param cliff Cliff period in seconds before vesting starts
     * @param duration Total vesting duration in seconds
     * @param referralBps Referral reward in basis points
     */
    function addPackage(
        string calldata name,
        uint256 entryUSDT,
        uint256 exchangeRate,
        uint16  vestBps,
        uint64  cliff,
        uint64  duration,
        uint16  referralBps
    ) external onlyAdmin whenNotPaused {
        require(bytes(name).length > 0, "PackageManager: Empty name");
        require(entryUSDT > 0, "PackageManager: Invalid entry amount");
        require(exchangeRate > 0, "PackageManager: Invalid exchange rate");
        require(vestBps <= 10000, "PackageManager: Invalid vest percentage");
        require(referralBps <= 1000, "PackageManager: Referral rate too high"); // Max 10%
        require(duration > 0, "PackageManager: Invalid duration");

        uint256 id = nextPackageId++;
        _packages[id] = Package({
            name: name,
            entryUSDT: entryUSDT,
            exchangeRate: exchangeRate,
            vestBps: vestBps,
            cliff: cliff,
            duration: duration,
            referralBps: referralBps,
            active: true,
            exists: true
        });
        _packageIds.push(id);

        emit PackageAdded(id, name, entryUSDT, exchangeRate, vestBps, cliff, duration, referralBps);
    }

    /**
     * @dev Toggle package active status
     * @param id Package ID to toggle
     */
    function togglePackage(uint256 id) external onlyAdmin validPackage(id) {
        _packages[id].active = !_packages[id].active;
        emit PackageToggled(id, _packages[id].active);
    }

    /**
     * @dev Update exchange rate for an existing package
     * @param id Package ID to update
     * @param newExchangeRate New exchange rate: USDT per BLOCKS for user token allocation
     */
    function setPackageExchangeRate(uint256 id, uint256 newExchangeRate)
        external
        onlyAdmin
        validPackage(id)
    {
        require(newExchangeRate > 0, "PackageManager: Invalid exchange rate");

        uint256 oldExchangeRate = _packages[id].exchangeRate;
        _packages[id].exchangeRate = newExchangeRate;

        emit PackageExchangeRateUpdated(id, oldExchangeRate, newExchangeRate);
    }

    /**
     * @dev Set global target price for liquidity operations
     * @param newGlobalTargetPrice New global target price in wei precision
     */
    function setGlobalTargetPrice(uint256 newGlobalTargetPrice)
        external
        onlyAdmin
    {
        require(newGlobalTargetPrice > 0, "PackageManager: Invalid global target price");

        uint256 oldGlobalTargetPrice = globalTargetPrice;
        globalTargetPrice = newGlobalTargetPrice;

        emit GlobalTargetPriceUpdated(oldGlobalTargetPrice, newGlobalTargetPrice);
    }

    /**
     * @dev Get package information by ID
     * @param id Package ID
     * @return Package struct containing all package details
     */
    function getPackage(uint256 id) external view validPackage(id) returns (Package memory) {
        return _packages[id];
    }

    /**
     * @dev Get all package IDs
     * @return Array of all package IDs
     */
    function getPackageIds() external view returns (uint256[] memory) {
        return _packageIds;
    }

    /**
     * @dev Get active package IDs only
     * @return Array of active package IDs
     */
    function getActivePackageIds() external view returns (uint256[] memory) {
        uint256 activeCount = 0;

        // Count active packages
        for (uint256 i = 0; i < _packageIds.length; i++) {
            if (_packages[_packageIds[i]].active) {
                activeCount++;
            }
        }

        // Create array of active package IDs
        uint256[] memory activeIds = new uint256[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < _packageIds.length; i++) {
            if (_packages[_packageIds[i]].active) {
                activeIds[index] = _packageIds[i];
                index++;
            }
        }

        return activeIds;
    }

    /**
     * @dev Get total number of packages
     * @return Total package count
     */
    function getPackageCount() external view returns (uint256) {
        return _packageIds.length;
    }

    /**
     * @dev Get user's portfolio statistics
     * @param user User address
     * @return UserStats struct containing aggregated user data
     */
    function getUserStats(address user) external view returns (UserStats memory) {
        return _userStats[user];
    }

    /**
     * @dev Get user's purchase history
     * @param user User address
     * @return Array of UserPurchase structs
     */
    function getUserPurchases(address user) external view returns (UserPurchase[] memory) {
        return _userPurchases[user];
    }

    /**
     * @dev Get user's purchase count
     * @param user User address
     * @return Number of purchases made by user
     */
    function getUserPurchaseCount(address user) external view returns (uint256) {
        return _userPurchases[user].length;
    }

    /**
     * @dev Get user's specific purchase by index
     * @param user User address
     * @param index Purchase index
     * @return UserPurchase struct
     */
    function getUserPurchase(address user, uint256 index) external view returns (UserPurchase memory) {
        require(index < _userPurchases[user].length, "PackageManager: Invalid purchase index");
        return _userPurchases[user][index];
    }

    /**
     * @dev Get user's redemption history
     * @param user User address
     * @return amounts Array of LP token amounts redeemed
     * @return timestamps Array of redemption timestamps
     */
    function getUserRedemptions(address user) external view returns (uint256[] memory amounts, uint256[] memory timestamps) {
        return (_userRedemptions[user], _userRedemptionTimestamps[user]);
    }

    /**
     * @dev Get user's redemption count
     * @param user User address
     * @return Number of redemptions made by user
     */
    function getUserRedemptionCount(address user) external view returns (uint256) {
        return _userRedemptions[user].length;
    }

    /**
     * @dev Get user's packages (alias for getUserPurchases for frontend compatibility)
     * @param user User address
     * @return Array of UserPurchase structs representing user's packages
     */
    function getUserPackages(address user) external view returns (UserPurchase[] memory) {
        return _userPurchases[user];
    }

    /**
     * @dev Get user's package count (alias for getUserPurchaseCount for frontend compatibility)
     * @param user User address
     * @return Number of packages purchased by user
     */
    function getUserPackageCount(address user) external view returns (uint256) {
        return _userPurchases[user].length;
    }

    /**
     * @dev Get packages by owner (alias for getUserPurchases for frontend compatibility)
     * @param owner Owner address
     * @return Array of UserPurchase structs representing owner's packages
     */
    function getPackagesByOwner(address owner) external view returns (UserPurchase[] memory) {
        return _userPurchases[owner];
    }

    /**
     * @dev Purchase an investment package
     * @param id Package ID to purchase
     * @param referrer Address of referrer (can be zero address)
     */
    function purchase(uint256 id, address referrer)
        external
        nonReentrant
        whenNotPaused
        validPackage(id)
    {
        Package memory pkg = _packages[id];
        require(pkg.active, "PackageManager: Package not active");
        require(referrer != msg.sender, "PackageManager: Cannot refer yourself");

        // Transfer USDT from user
        usdt.safeTransferFrom(msg.sender, address(this), pkg.entryUSDT);

        // Apply purchase tax if configured
        uint256 netUSDT = _applyTax(PURCHASE_TAX_KEY, pkg.entryUSDT);

        // Dual pricing system implementation
        // Step 1: Calculate total user BLOCKS tokens based on package exchange rate
        // exchangeRate is in 18-decimal precision (USDT per BLOCKS) for V2 architecture
        // Formula: totalTokens = (netUSDT * 10^18) / exchangeRate
        // Both USDT and BLOCKS use 18 decimals in V2 architecture
        uint8 usdtDecimals = usdt.decimals();
        require(usdtDecimals == 18, "PackageManager: USDT must use 18 decimals for V2 architecture");

        // Direct calculation since both tokens use 18 decimals
        uint256 totalUserTokens = (netUSDT * 1e18) / pkg.exchangeRate;

        // Step 2: Calculate USDT allocation based on vestBps
        uint256 usdtForPool = (netUSDT * (10_000 - pkg.vestBps)) / 10_000;
        uint256 usdtForVault = netUSDT - usdtForPool;

        // Step 3: Calculate vesting and pool token allocation
        uint256 vestTokens = (totalUserTokens * pkg.vestBps) / 10_000;
        uint256 poolTokens = totalUserTokens - vestTokens;

        // Step 4: Calculate treasury allocation (5% of user claimable tokens)
        uint256 treasuryTokens = (totalUserTokens * 500) / 10_000; // 5% of total user tokens

        // Handle vesting tokens
        if (vestTokens > 0) {
            shareToken.mint(address(vestingVault), vestTokens);
            vestingVault.lock(msg.sender, vestTokens, pkg.cliff, pkg.duration);
        }

        // Handle treasury BLOCKS allocation (for operational sustainability)
        if (treasuryTokens > 0) {
            shareToken.mint(treasury, treasuryTokens);
            emit TreasuryBlocksAllocated(msg.sender, id, treasuryTokens);
        }

        // Transfer vault portion to treasury
        if (usdtForVault > 0) {
            usdt.safeTransfer(treasury, usdtForVault);
        }

        // Handle liquidity provision using global target price with enhanced error handling
        uint256 liquidity = 0;
        if (usdtForPool > 0 && globalTargetPrice > 0) {
            // Calculate BLOCKS tokens for liquidity based on global target price
            // Both USDT and globalTargetPrice use 18 decimals in V2 architecture
            uint256 liquidityBLOCKS = (usdtForPool * 1e18) / globalTargetPrice;

            // Mint BLOCKS tokens for liquidity
            shareToken.mint(address(this), liquidityBLOCKS);

            // Use enhanced liquidity addition with comprehensive error handling
            liquidity = _addLiquidityWithProtection(
                msg.sender,
                id,
                usdtForPool,
                liquidityBLOCKS
            );

            // If liquidity addition failed, BLOCKS tokens were already minted but USDT was sent to treasury
            // We need to handle the excess BLOCKS tokens
            if (liquidity == 0) {
                // Send the minted BLOCKS tokens to treasury as well
                IERC20(address(shareToken)).transfer(treasury, liquidityBLOCKS);
            }
        }

        // Mint LP tokens to user for their claimable tokens (total user tokens)
        // Treasury allocation is not claimable by user, so not included in LP tokens
        uint256 userClaimableTokens = totalUserTokens;
        if (userClaimableTokens > 0) {
            lpToken.mint(msg.sender, userClaimableTokens);
        }

        // Handle referral rewards
        uint256 referralReward = 0;
        if (referrer != address(0) && pkg.referralBps > 0) {
            referralReward = (totalUserTokens * pkg.referralBps) / 10_000;

            // Mint referral reward directly to referrer instead of using treasury
            // This ensures referral rewards are always available and don't depend on treasury balance
            shareToken.mint(referrer, referralReward);

            // Update referrer's stats
            _userStats[referrer].totalReferralRewards += referralReward;

            // Emit dedicated referral event
            emit ReferralPaid(referrer, msg.sender, referralReward);
        }

        // Update user's portfolio data
        // LP tokens represent user's claimable tokens (total user tokens)
        // Pass totalUserTokens instead of totalTokens to exclude treasury allocation from user stats
        _updateUserPurchaseData(msg.sender, id, pkg.entryUSDT, totalUserTokens, vestTokens, poolTokens, userClaimableTokens, referrer, referralReward);

        emit Purchased(
            msg.sender,
            id,
            pkg.entryUSDT,
            totalUserTokens, // User-claimable tokens only, excluding treasury allocation
            vestTokens,
            poolTokens,
            userClaimableTokens, // LP tokens for user's claimable portion
            referrer,
            referralReward
        );
    }

    /**
     * @dev Redeem LP tokens (simplified version - actual implementation may vary)
     * @param lpAmount Amount of LP tokens to redeem
     */
    function redeem(uint256 lpAmount) external nonReentrant whenNotPaused {
        require(lpAmount > 0, "PackageManager: Invalid LP amount");

        // Burn LP tokens from user
        lpToken.burn(msg.sender, lpAmount);

        // Trigger vesting claim for user
        vestingVault.claim();

        // Update user's redemption data
        _updateUserRedemptionData(msg.sender, lpAmount);

        emit Redeemed(msg.sender, lpAmount);
    }

    /**
     * @dev Enhanced LP token redemption with actual liquidity removal from PancakeSwap
     * @param lpAmount Amount of LP tokens to redeem
     * @param amountShareMin Minimum amount of ShareToken to receive (slippage protection)
     * @param amountUSDTMin Minimum amount of USDT to receive (slippage protection)
     * @param deadline Transaction deadline timestamp
     */
    function redeemWithLiquidityRemoval(
        uint256 lpAmount,
        uint256 amountShareMin,
        uint256 amountUSDTMin,
        uint256 deadline
    ) external nonReentrant whenNotPaused {
        require(lpAmount > 0, "PackageManager: Invalid LP amount");
        require(deadline >= block.timestamp, "PackageManager: Deadline expired");

        // Get the PancakeSwap pair address
        address pairAddress = factory.getPair(address(shareToken), address(usdt));
        require(pairAddress != address(0), "PackageManager: No liquidity pair found");

        // Calculate proportional liquidity to remove based on LP tokens
        uint256 pairLPBalance = IERC20(pairAddress).balanceOf(address(this));

        // Calculate the actual liquidity to remove from the pool
        uint256 liquidityToRemove = (lpAmount * pairLPBalance) / lpToken.totalSupply();
        require(liquidityToRemove > 0, "PackageManager: No liquidity to remove");

        // Burn LP tokens from user
        lpToken.burn(msg.sender, lpAmount);

        // Approve router to spend pair LP tokens
        IERC20(pairAddress).approve(address(router), liquidityToRemove);

        // Remove liquidity from PancakeSwap
        (uint256 amountShare, uint256 amountUSDT) = router.removeLiquidity(
            address(shareToken),
            address(usdt),
            liquidityToRemove,
            amountShareMin,
            amountUSDTMin,
            msg.sender, // Send tokens directly to user
            deadline
        );

        // Trigger vesting claim for user
        vestingVault.claim();

        // Update user's redemption data
        _updateUserRedemptionData(msg.sender, lpAmount);

        emit LiquidityRedeemed(msg.sender, lpAmount, liquidityToRemove, amountShare, amountUSDT);
    }

    /**
     * @dev Get expected returns for LP token redemption
     * @param lpAmount Amount of LP tokens to redeem
     * @return expectedShare Expected ShareToken amount
     * @return expectedUSDT Expected USDT amount
     * @return liquidityToRemove Actual liquidity amount to remove from pool
     */
    function getRedemptionPreview(uint256 lpAmount)
        external
        view
        returns (uint256 expectedShare, uint256 expectedUSDT, uint256 liquidityToRemove)
    {
        require(lpAmount > 0, "PackageManager: Invalid LP amount");

        // Get the PancakeSwap pair address
        address pairAddress = factory.getPair(address(shareToken), address(usdt));
        if (pairAddress == address(0)) {
            return (0, 0, 0);
        }

        IPancakePair pair = IPancakePair(pairAddress);
        uint256 pairLPBalance = IERC20(pairAddress).balanceOf(address(this));
        uint256 totalLPTokens = lpToken.totalSupply();

        if (totalLPTokens == 0 || pairLPBalance == 0) {
            return (0, 0, 0);
        }

        // Calculate liquidity to remove
        liquidityToRemove = (lpAmount * pairLPBalance) / totalLPTokens;

        // Get current reserves
        (uint112 reserve0, uint112 reserve1,) = pair.getReserves();
        uint256 totalPairSupply = pair.totalSupply();

        if (totalPairSupply == 0) {
            return (0, 0, liquidityToRemove);
        }

        // Calculate proportional amounts
        uint256 amount0 = (liquidityToRemove * reserve0) / totalPairSupply;
        uint256 amount1 = (liquidityToRemove * reserve1) / totalPairSupply;

        // Determine which token is which
        address token0 = pair.token0();
        if (token0 == address(shareToken)) {
            expectedShare = amount0;
            expectedUSDT = amount1;
        } else {
            expectedShare = amount1;
            expectedUSDT = amount0;
        }
    }

    /**
     * @dev Internal function to apply tax if configured
     * @param taxKey Tax bucket key
     * @param amount Amount to apply tax on
     * @return Net amount after tax deduction
     */
    function _applyTax(bytes32 taxKey, uint256 amount) internal returns (uint256) {
        (uint16 rateBps, address recipient) = taxManager.buckets(taxKey);

        if (rateBps > 0 && recipient != address(0)) {
            uint256 taxAmount = (amount * rateBps) / 10_000;
            if (taxAmount > 0) {
                usdt.safeTransfer(recipient, taxAmount);
                emit TaxApplied(taxKey, taxAmount, recipient);
                return amount - taxAmount;
            }
        }

        return amount;
    }

    // Admin functions

    /**
     * @dev Pause the contract
     */
    function pause() external onlyAdmin {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyAdmin {
        _unpause();
    }

    /**
     * @dev Update treasury address
     * @param newTreasury New treasury address
     */
    function setTreasury(address newTreasury) external onlyAdmin {
        require(newTreasury != address(0), "PackageManager: Invalid treasury address");
        address oldTreasury = treasury;
        treasury = newTreasury;
        emit TreasuryUpdated(oldTreasury, newTreasury);
    }

    /**
     * @dev Get treasury's BLOCKS token balance
     * @return Treasury's current BLOCKS balance
     */
    function getTreasuryBlocksBalance() external view returns (uint256) {
        return IERC20(address(shareToken)).balanceOf(treasury);
    }

    /**
     * @dev Get treasury's BLOCKS allowance to this contract (deprecated - referrals now mint directly)
     * @return Current allowance amount
     */
    function getTreasuryBlocksAllowance() external view returns (uint256) {
        return IERC20(address(shareToken)).allowance(treasury, address(this));
    }

    /**
     * @dev Update deadline window for DEX operations
     * @param newWindow New deadline window in seconds
     */
    function setDeadlineWindow(uint256 newWindow) external onlyAdmin {
        require(newWindow >= 60, "PackageManager: Deadline too short"); // Minimum 1 minute
        require(newWindow <= 3600, "PackageManager: Deadline too long"); // Maximum 1 hour
        uint256 oldWindow = deadlineWindow;
        deadlineWindow = newWindow;
        emit DeadlineWindowUpdated(oldWindow, newWindow);
    }



    /**
     * @dev Emergency function to recover stuck tokens
     * @param token Token address to recover
     * @param amount Amount to recover
     */
    function emergencyRecoverToken(address token, uint256 amount) external onlyAdmin {
        require(token != address(0), "PackageManager: Invalid token address");
        require(amount > 0, "PackageManager: Invalid amount");

        IERC20(token).transfer(treasury, amount);
    }

    /**
     * @dev Internal function to update user's purchase data
     * @param user User address
     * @param packageId Package ID purchased
     * @param usdtAmount USDT amount invested
     * @param totalTokens Total user-claimable tokens received (excluding treasury allocation)
     * @param vestTokens Tokens sent to vesting
     * @param poolTokens Tokens used for liquidity
     * @param lpTokens LP tokens received
     * @param referrer Referrer address
     * @param referralReward Referral reward amount
     */
    function _updateUserPurchaseData(
        address user,
        uint256 packageId,
        uint256 usdtAmount,
        uint256 totalTokens,
        uint256 vestTokens,
        uint256 poolTokens,
        uint256 lpTokens,
        address referrer,
        uint256 referralReward
    ) internal {
        // Update user stats
        UserStats storage stats = _userStats[user];
        stats.totalInvested += usdtAmount;
        stats.totalTokensReceived += totalTokens;
        stats.totalVestTokens += vestTokens;
        stats.totalPoolTokens += poolTokens;
        stats.totalLPTokens += lpTokens;
        stats.purchaseCount += 1;

        // Add purchase record
        _userPurchases[user].push(UserPurchase({
            packageId: packageId,
            usdtAmount: usdtAmount,
            totalTokens: totalTokens,
            vestTokens: vestTokens,
            poolTokens: poolTokens,
            lpTokens: lpTokens,
            referrer: referrer,
            referralReward: referralReward,
            timestamp: block.timestamp
        }));
    }

    /**
     * @dev Internal function to update user's redemption data
     * @param user User address
     * @param lpAmount LP tokens redeemed
     */
    function _updateUserRedemptionData(address user, uint256 lpAmount) internal {
        // Update user stats
        UserStats storage stats = _userStats[user];
        stats.redemptionCount += 1;
        stats.totalRedemptions += lpAmount;

        // Add redemption record
        _userRedemptions[user].push(lpAmount);
        _userRedemptionTimestamps[user].push(block.timestamp);
    }

    /**
     * @dev Enhanced internal function for adding liquidity with comprehensive error handling
     * @param user User address for event emission
     * @param packageId Package ID for event emission
     * @param usdtAmount USDT amount to add to liquidity
     * @param blocksAmount BLOCKS amount to add to liquidity
     * @return liquidity Amount of liquidity tokens received (0 if failed)
     */
    function _addLiquidityWithProtection(
        address user,
        uint256 packageId,
        uint256 usdtAmount,
        uint256 blocksAmount
    ) internal returns (uint256 liquidity) {
        // Calculate minimum amounts with slippage protection
        uint256 minUSDT = (usdtAmount * (10000 - slippageTolerance)) / 10000;
        uint256 minBLOCKS = (blocksAmount * (10000 - slippageTolerance)) / 10000;

        // Emit slippage protection event for transparency
        emit SlippageProtectionTriggered(
            user,
            packageId,
            blocksAmount,
            usdtAmount,
            minBLOCKS,
            minUSDT
        );

        // Approve router for token transfers
        require(usdt.approve(address(router), usdtAmount), "PackageManager: USDT approve failed");
        require(IERC20(address(shareToken)).approve(address(router), blocksAmount), "PackageManager: BLOCKS approve failed");

        // Attempt to add liquidity with comprehensive error handling
        try router.addLiquidity(
            address(shareToken),
            address(usdt),
            blocksAmount,
            usdtAmount,
            minBLOCKS,
            minUSDT,
            address(this),
            block.timestamp + deadlineWindow
        ) returns (uint256 actualShareToken, uint256 actualUSDT, uint256 liquidityTokens) {
            // Verify liquidity was actually added
            require(liquidityTokens > 0, "PackageManager: No liquidity tokens received");

            // Emit success event
            emit LiquidityAdded(
                user,
                packageId,
                blocksAmount,
                usdtAmount,
                liquidityTokens,
                actualShareToken,
                actualUSDT
            );

            return liquidityTokens;
        } catch Error(string memory reason) {
            // Handle known errors with fallback mechanism
            _handleLiquidityFailure(user, packageId, usdtAmount, reason);
            return 0;
        } catch (bytes memory lowLevelData) {
            // Handle low-level errors
            string memory reason = lowLevelData.length > 0
                ? string(lowLevelData)
                : "Unknown low-level error";
            _handleLiquidityFailure(user, packageId, usdtAmount, reason);
            return 0;
        }
    }

    /**
     * @dev Internal function to handle liquidity addition failures
     * @param user User address
     * @param packageId Package ID
     * @param usdtAmount USDT amount that failed to be added
     * @param reason Failure reason
     */
    function _handleLiquidityFailure(
        address user,
        uint256 packageId,
        uint256 usdtAmount,
        string memory reason
    ) internal {
        // Fallback: send USDT to treasury instead of adding to liquidity
        usdt.safeTransfer(treasury, usdtAmount);

        // Emit failure event for transparency and monitoring
        emit LiquidityAdditionFailed(user, packageId, usdtAmount, reason);
    }

    /**
     * @dev Admin function to update slippage tolerance
     * @param newTolerance New slippage tolerance in basis points (e.g., 500 = 5%)
     */
    function setSlippageTolerance(uint256 newTolerance) external onlyAdmin {
        require(newTolerance <= 1000, "PackageManager: Slippage tolerance too high"); // Max 10%
        require(newTolerance >= 100, "PackageManager: Slippage tolerance too low"); // Min 1%
        uint256 oldTolerance = slippageTolerance;
        slippageTolerance = newTolerance;
        emit SlippageToleranceUpdated(oldTolerance, newTolerance);
    }

    // Event for slippage tolerance updates
    event SlippageToleranceUpdated(uint256 oldTolerance, uint256 newTolerance);
}

contracts/PackageManagerV2_Fixed.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

// Interfaces for external contracts
interface IERC20Decimals is IERC20 {
    function decimals() external view returns (uint8);
}

interface IBLOCKS is IERC20 {
    function mint(address to, uint256 amount) external;
}

interface IBLOCKS_LP is IERC20 {
    function mint(address to, uint256 amount) external;
}

interface IVestingVault {
    function lock(address user, uint256 amount, uint64 cliff, uint64 duration) external;
}

interface IPancakeRouter {
    function addLiquidity(
        address tokenA, address tokenB, uint256 amountADesired, uint256 amountBDesired,
        uint256 amountAMin, uint256 amountBMin, address to, uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 liquidity);

    function factory() external pure returns (address);
}

interface IPancakeFactory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface ISwapTaxManager {
    function buckets(bytes32 key) external view returns (uint16 rateBps, address recipient);
}

/**
 * @title PackageManagerV2_Fixed
 * @dev Fixed version of PackageManager with corrected business logic:
 *      - Referral rewards paid from Treasury (not minted)
 *      - 70/30 USDT split (70% treasury, 30% liquidity)
 *      - Global target price affects only LP allocation
 *      - 1:1 BLOCKS-LP token distribution
 */
contract PackageManagerV2_Fixed is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    using SafeERC20 for IERC20Decimals;

    bytes32 public constant ADMIN_ROLE = DEFAULT_ADMIN_ROLE;
    bytes32 public constant PACKAGE_MANAGER_ROLE = keccak256("PACKAGE_MANAGER_ROLE");

    // Tax bucket keys for SwapTaxManager integration
    bytes32 public constant PURCHASE_TAX_KEY = keccak256("PURCHASE");
    bytes32 public constant REFERRAL_TAX_KEY = keccak256("REFERRAL");

    struct Package {
        string   name;
        uint256  entryUSDT;
        uint256  exchangeRate;     // Exchange rate: USDT per BLOCKS for user token allocation
        uint16   vestBps;          // Percentage of user tokens to vest (basis points)
        uint64   cliff;            // Cliff period in seconds
        uint64   duration;         // Total vesting duration in seconds
        uint16   referralBps;      // Referral reward percentage (basis points)
        bool     active;
        bool     exists;
    }

    struct UserPurchase {
        uint256 packageId;
        uint256 usdtAmount;
        uint256 totalTokens;
        uint256 vestTokens;
        uint256 poolTokens;
        uint256 lpTokens;
        address referrer;
        uint256 referralReward;
        uint256 timestamp;
    }

    struct UserStats {
        uint256 totalInvested;
        uint256 totalTokensReceived;
        uint256 totalVestTokens;
        uint256 totalPoolTokens;
        uint256 totalLPTokens;
        uint256 totalReferralRewards;
        uint256 purchaseCount;
    }

    struct ReferralEarning {
        address buyer;
        uint256 packageId;
        uint256 reward;
        uint256 timestamp;
        uint256 blockNumber;
    }

    // State variables
    IERC20Decimals   public immutable usdt;
    IBLOCKS          public immutable shareToken;
    IBLOCKS_LP       public immutable lpToken;
    IVestingVault    public immutable vestingVault;
    IPancakeRouter   public immutable router;
    IPancakeFactory  public immutable factory;
    ISwapTaxManager  public immutable taxManager;
    address          public           treasury;
    uint256          public           globalTargetPrice; // Global target price for liquidity operations only
    uint256          public           slippageToleranceBps = 500; // 5% slippage tolerance
    uint256          public           treasuryAllocationBps = 7000; // 70% to treasury
    uint256          public           treasuryMinimumThreshold = 1000 * 1e18; // Minimum BLOCKS in treasury

    uint256   public nextPackageId;
    uint256[] private _packageIds;
    mapping(uint256 => Package) private _packages;
    mapping(address => UserStats) private _userStats;
    mapping(address => UserPurchase[]) private _userPurchases;
    mapping(address => mapping(uint256 => bool)) private _userPackagePurchased;
    mapping(address => uint256) public pendingReferralRewards; // Track pending referral rewards
    mapping(address => ReferralEarning[]) private _referralEarnings;

    // Events
    event PackageAdded(
        uint256 indexed id, string name, uint256 entryUSDT, uint256 exchangeRate,
        uint16 vestBps, uint64 cliff, uint64 duration, uint16 referralBps
    );

    event PackagePurchased(
        address indexed user, uint256 indexed packageId, uint256 usdtAmount,
        uint256 totalTokens, uint256 vestTokens, uint256 poolTokens, uint256 lpTokens,
        address referrer, uint256 referralReward
    );

    event LiquidityAdded(
        address indexed user, uint256 indexed packageId,
        uint256 usdtAmount, uint256 blocksAmount, uint256 liquidity
    );

    event ReferralRewardPaid(
        address indexed referrer, address indexed buyer,
        uint256 indexed packageId, uint256 reward
    );

    event ReferralPaymentSkipped(
        address indexed referrer, address indexed buyer, uint256 indexed packageId,
        uint256 rewardAmount, uint256 treasuryBalance
    );

    event PendingReferralAdded(
        address indexed referrer, uint256 amount, uint256 totalPending
    );

    event GlobalTargetPriceUpdated(uint256 oldPrice, uint256 newPrice);
    event TreasuryThresholdUpdated(uint256 oldThreshold, uint256 newThreshold);
    event TreasuryLowBalance(uint256 currentBalance, uint256 threshold);
    event InsufficientTreasuryFunds(uint256 required, uint256 available);

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "PackageManager: Admin role required");
        _;
    }

    modifier validPackage(uint256 id) {
        require(_packages[id].exists, "PackageManager: Package does not exist");
        _;
    }

    /**
     * @dev Constructor
     * @param usdt_ USDT token contract address (must be 18 decimals)
     * @param shareToken_ BLOCKS token contract address
     * @param lpToken_ BLOCKS-LP token contract address
     * @param vestingVault_ Vesting vault contract address
     * @param router_ PancakeSwap router contract address
     * @param factory_ PancakeSwap factory contract address
     * @param treasury_ Treasury address for receiving funds
     * @param taxManager_ Tax manager contract address
     * @param admin Admin address for access control
     * @param initialGlobalTargetPrice_ Initial global target price for liquidity operations (18 decimals)
     */
    constructor(
        address usdt_,
        address shareToken_,
        address lpToken_,
        address vestingVault_,
        address router_,
        address factory_,
        address treasury_,
        address taxManager_,
        address admin,
        uint256 initialGlobalTargetPrice_
    ) {
        require(usdt_ != address(0), "Invalid USDT address");
        require(shareToken_ != address(0), "Invalid share token address");
        require(lpToken_ != address(0), "Invalid LP token address");
        require(vestingVault_ != address(0), "Invalid vault address");
        require(router_ != address(0), "Invalid router address");
        require(factory_ != address(0), "Invalid factory address");
        require(treasury_ != address(0), "Invalid treasury address");
        require(taxManager_ != address(0), "Invalid tax manager address");
        require(admin != address(0), "Invalid admin address");
        require(initialGlobalTargetPrice_ > 0, "Invalid initial global target price");

        usdt         = IERC20Decimals(usdt_);
        shareToken   = IBLOCKS(shareToken_);
        lpToken      = IBLOCKS_LP(lpToken_);
        vestingVault = IVestingVault(vestingVault_);
        router       = IPancakeRouter(router_);
        factory      = IPancakeFactory(factory_);
        taxManager   = ISwapTaxManager(taxManager_);
        treasury     = treasury_;
        globalTargetPrice = initialGlobalTargetPrice_;

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PACKAGE_MANAGER_ROLE, admin);
    }

    /**
     * @dev Add a new investment package
     * @param name Package name
     * @param entryUSDT Entry amount in USDT (18 decimals)
     * @param exchangeRate Exchange rate: USDT per BLOCKS for user token allocation (18 decimals)
     * @param vestBps Percentage of user tokens to vest in basis points
     * @param cliff Cliff period in seconds before vesting starts
     * @param duration Total vesting duration in seconds
     * @param referralBps Referral reward in basis points (max 1000 = 10%)
     */
    function addPackage(
        string memory name,
        uint256 entryUSDT,
        uint256 exchangeRate,
        uint16 vestBps,
        uint64 cliff,
        uint64 duration,
        uint16 referralBps
    ) external onlyRole(PACKAGE_MANAGER_ROLE) {
        require(bytes(name).length > 0, "PackageManager: Empty name");
        require(entryUSDT > 0, "PackageManager: Invalid entry amount");
        require(exchangeRate > 0, "PackageManager: Invalid exchange rate");
        require(vestBps <= 10000, "PackageManager: Invalid vest percentage");
        require(referralBps <= 1000, "PackageManager: Referral rate too high"); // Max 10%
        require(duration > 0, "PackageManager: Invalid duration");

        uint256 id = nextPackageId++;
        _packages[id] = Package({
            name: name,
            entryUSDT: entryUSDT,
            exchangeRate: exchangeRate,
            vestBps: vestBps,
            cliff: cliff,
            duration: duration,
            referralBps: referralBps,
            active: true,
            exists: true
        });
        _packageIds.push(id);

        emit PackageAdded(id, name, entryUSDT, exchangeRate, vestBps, cliff, duration, referralBps);
    }

    /**
     * @dev Set global target price for liquidity operations
     * @param newPrice New global target price in wei precision (18 decimals)
     */
    function setGlobalTargetPrice(uint256 newPrice) external onlyAdmin {
        require(newPrice > 0, "PackageManager: Invalid global target price");
        uint256 oldPrice = globalTargetPrice;
        globalTargetPrice = newPrice;
        emit GlobalTargetPriceUpdated(oldPrice, newPrice);
    }

    /**
     * @dev Set treasury minimum threshold for referral payments
     * @param newThreshold New minimum threshold in BLOCKS tokens (18 decimals)
     */
    function setTreasuryMinimumThreshold(uint256 newThreshold) external onlyAdmin {
        uint256 oldThreshold = treasuryMinimumThreshold;
        treasuryMinimumThreshold = newThreshold;
        emit TreasuryThresholdUpdated(oldThreshold, newThreshold);
    }

    /**
     * @dev Purchase a package with corrected business logic
     * @param id Package ID to purchase
     * @param referrer Address of referrer (can be zero address)
     */
    function purchase(uint256 id, address referrer) external nonReentrant whenNotPaused validPackage(id) {
        Package storage pkg = _packages[id];
        require(pkg.active, "PackageManager: Package not active");
        require(!_userPackagePurchased[msg.sender][id], "PackageManager: Package already purchased");

        // Verify USDT decimals for V2 architecture
        uint8 usdtDecimals = usdt.decimals();
        require(usdtDecimals == 18, "PackageManager: USDT must use 18 decimals for V2 architecture");

        // Transfer USDT from user
        usdt.safeTransferFrom(msg.sender, address(this), pkg.entryUSDT);

        // Calculate user tokens based on package exchange rate
        uint256 totalUserTokens = (pkg.entryUSDT * 1e18) / pkg.exchangeRate;

        // FIXED: 70/30 USDT split (70% treasury, 30% liquidity) - NOT based on vestBps
        uint256 usdtForTreasury = (pkg.entryUSDT * treasuryAllocationBps) / 10_000; // 70%
        uint256 usdtForPool = pkg.entryUSDT - usdtForTreasury; // 30%

        // Calculate vesting and immediate token allocation based on vestBps
        uint256 vestTokens = (totalUserTokens * pkg.vestBps) / 10_000;
        uint256 immediateTokens = totalUserTokens - vestTokens;

        // Handle vesting tokens
        if (vestTokens > 0) {
            shareToken.mint(address(vestingVault), vestTokens);
            vestingVault.lock(msg.sender, vestTokens, pkg.cliff, pkg.duration);
        }

        // Transfer treasury portion to treasury
        if (usdtForTreasury > 0) {
            usdt.safeTransfer(treasury, usdtForTreasury);
        }

        // Handle liquidity provision using global target price
        uint256 lpTokens = 0;
        if (usdtForPool > 0 && globalTargetPrice > 0) {
            // Calculate BLOCKS tokens for liquidity based on global target price
            uint256 liquidityBLOCKS = (usdtForPool * 1e18) / globalTargetPrice;

            // Mint BLOCKS tokens for liquidity
            shareToken.mint(address(this), liquidityBLOCKS);

            // Add liquidity to DEX
            uint256 liquidity = _addLiquidity(usdtForPool, liquidityBLOCKS);

            // FIXED: 1:1 BLOCKS-LP token distribution (equal to BLOCKS tokens received)
            lpTokens = liquidityBLOCKS; // 1:1 ratio
            lpToken.mint(msg.sender, lpTokens);

            emit LiquidityAdded(msg.sender, id, usdtForPool, liquidityBLOCKS, liquidity);
        }

        // FIXED: Handle referral rewards from Treasury (not minting)
        uint256 referralReward = 0;
        if (referrer != address(0) && pkg.referralBps > 0) {
            referralReward = (totalUserTokens * pkg.referralBps) / 10_000;

            // Check treasury balance
            uint256 treasuryBalance = IERC20(address(shareToken)).balanceOf(treasury);

            if (treasuryBalance >= referralReward && treasuryBalance >= treasuryMinimumThreshold) {
                // Pay referral reward from treasury
                // Note: Treasury must have approved this contract to spend BLOCKS tokens
                IERC20(address(shareToken)).transferFrom(treasury, referrer, referralReward);

                // Update referrer's stats
                _userStats[referrer].totalReferralRewards += referralReward;

                // Record referral earning
                _referralEarnings[referrer].push(ReferralEarning({
                    buyer: msg.sender,
                    packageId: id,
                    reward: referralReward,
                    timestamp: block.timestamp,
                    blockNumber: block.number
                }));

                emit ReferralRewardPaid(referrer, msg.sender, id, referralReward);
            } else {
                // Add to pending referrals
                pendingReferralRewards[referrer] += referralReward;
                emit PendingReferralAdded(referrer, referralReward, pendingReferralRewards[referrer]);
                emit ReferralPaymentSkipped(referrer, msg.sender, id, referralReward, treasuryBalance);

                // Emit low balance warning
                if (treasuryBalance < treasuryMinimumThreshold) {
                    emit TreasuryLowBalance(treasuryBalance, treasuryMinimumThreshold);
                }
            }
        }

        // Update user stats
        _userStats[msg.sender].totalInvested += pkg.entryUSDT;
        _userStats[msg.sender].totalTokensReceived += totalUserTokens;
        _userStats[msg.sender].totalVestTokens += vestTokens;
        _userStats[msg.sender].totalPoolTokens += immediateTokens;
        _userStats[msg.sender].totalLPTokens += lpTokens;
        _userStats[msg.sender].purchaseCount++;

        // Record purchase
        _userPurchases[msg.sender].push(UserPurchase({
            packageId: id,
            usdtAmount: pkg.entryUSDT,
            totalTokens: totalUserTokens,
            vestTokens: vestTokens,
            poolTokens: immediateTokens,
            lpTokens: lpTokens,
            referrer: referrer,
            referralReward: referralReward,
            timestamp: block.timestamp
        }));

        _userPackagePurchased[msg.sender][id] = true;

        emit PackagePurchased(
            msg.sender, id, pkg.entryUSDT, totalUserTokens,
            vestTokens, immediateTokens, lpTokens, referrer, referralReward
        );
    }

    /**
     * @dev Add liquidity to PancakeSwap DEX
     * @param usdtAmount Amount of USDT to add
     * @param blocksAmount Amount of BLOCKS to add
     * @return liquidity Amount of LP tokens received
     */
    function _addLiquidity(uint256 usdtAmount, uint256 blocksAmount) internal returns (uint256 liquidity) {
        // Approve tokens for router
        usdt.approve(address(router), usdtAmount);
        shareToken.approve(address(router), blocksAmount);

        // Calculate minimum amounts with slippage tolerance
        uint256 usdtMin = (usdtAmount * (10_000 - slippageToleranceBps)) / 10_000;
        uint256 blocksMin = (blocksAmount * (10_000 - slippageToleranceBps)) / 10_000;

        // Add liquidity
        (, , liquidity) = router.addLiquidity(
            address(usdt),
            address(shareToken),
            usdtAmount,
            blocksAmount,
            usdtMin,
            blocksMin,
            address(this), // LP tokens go to contract (not distributed to users)
            block.timestamp + 300
        );
    }

    /**
     * @dev Process pending referral rewards for a referrer
     * @param referrer Address of the referrer
     * @return amountPaid Amount of BLOCKS tokens paid
     */
    function processPendingReferrals(address referrer) external onlyAdmin returns (uint256 amountPaid) {
        uint256 pendingAmount = pendingReferralRewards[referrer];
        require(pendingAmount > 0, "PackageManager: No pending referrals");

        uint256 treasuryBalance = IERC20(address(shareToken)).balanceOf(treasury);
        require(treasuryBalance >= pendingAmount, "PackageManager: Insufficient treasury balance");

        // Pay pending referrals
        IERC20(address(shareToken)).transferFrom(treasury, referrer, pendingAmount);

        // Update stats
        _userStats[referrer].totalReferralRewards += pendingAmount;

        // Clear pending amount
        pendingReferralRewards[referrer] = 0;

        return pendingAmount;
    }

    /**
     * @dev Emergency treasury top-up function
     * @param amount Amount of BLOCKS tokens to mint to treasury
     */
    function emergencyTreasuryTopUp(uint256 amount) external onlyAdmin {
        require(amount > 0, "PackageManager: Invalid amount");
        shareToken.mint(treasury, amount);
        emit InsufficientTreasuryFunds(amount, IERC20(address(shareToken)).balanceOf(treasury));
    }

    // View functions
    function getUserStats(address user) external view returns (UserStats memory) {
        return _userStats[user];
    }

    function getUserPurchases(address user) external view returns (UserPurchase[] memory) {
        return _userPurchases[user];
    }

    function getUserPurchaseCount(address user) external view returns (uint256) {
        return _userPurchases[user].length;
    }

    function getPackage(uint256 id) external view returns (Package memory) {
        require(_packages[id].exists, "PackageManager: Package does not exist");
        return _packages[id];
    }

    function getAllPackages() external view returns (Package[] memory) {
        Package[] memory packages = new Package[](_packageIds.length);
        for (uint256 i = 0; i < _packageIds.length; i++) {
            packages[i] = _packages[_packageIds[i]];
        }
        return packages;
    }

    function getActivePackages() external view returns (Package[] memory) {
        uint256 activeCount = 0;
        for (uint256 i = 0; i < _packageIds.length; i++) {
            if (_packages[_packageIds[i]].active) {
                activeCount++;
            }
        }

        Package[] memory activePackages = new Package[](activeCount);
        uint256 index = 0;
        for (uint256 i = 0; i < _packageIds.length; i++) {
            if (_packages[_packageIds[i]].active) {
                activePackages[index] = _packages[_packageIds[i]];
                index++;
            }
        }
        return activePackages;
    }

    function getPendingReferralAmount(address referrer) external view returns (uint256) {
        return pendingReferralRewards[referrer];
    }

    function getReferralEarnings(address referrer) external view returns (ReferralEarning[] memory) {
        return _referralEarnings[referrer];
    }

    function getTreasuryBalanceStatus() external view returns (uint256 currentBalance, uint256 minimumThreshold, bool isLowBalance) {
        currentBalance = IERC20(address(shareToken)).balanceOf(treasury);
        minimumThreshold = treasuryMinimumThreshold;
        isLowBalance = currentBalance < minimumThreshold;
    }

    /**
     * @dev Calculate package token splits for preview
     * @param packageId Package ID
     * @return totalTokens Total BLOCKS tokens user will receive
     * @return vestTokens BLOCKS tokens that will be vested
     * @return poolTokens BLOCKS tokens for immediate use
     * @return lpTokens BLOCKS-LP tokens user will receive
     * @return referralRewardAmount BLOCKS tokens for referral reward
     */
    function calculatePackageSplits(uint256 packageId) external view validPackage(packageId) returns (
        uint256 totalTokens,
        uint256 vestTokens,
        uint256 poolTokens,
        uint256 lpTokens,
        uint256 referralRewardAmount
    ) {
        Package storage pkg = _packages[packageId];

        // Calculate user tokens based on package exchange rate
        totalTokens = (pkg.entryUSDT * 1e18) / pkg.exchangeRate;

        // Calculate vesting allocation
        vestTokens = (totalTokens * pkg.vestBps) / 10_000;
        poolTokens = totalTokens - vestTokens;

        // Calculate LP tokens (based on 30% USDT allocation and global target price)
        if (globalTargetPrice > 0) {
            uint256 usdtForPool = (pkg.entryUSDT * (10_000 - treasuryAllocationBps)) / 10_000; // 30%
            uint256 liquidityBLOCKS = (usdtForPool * 1e18) / globalTargetPrice;
            lpTokens = liquidityBLOCKS; // 1:1 ratio
        }

        // Calculate referral reward
        referralRewardAmount = (totalTokens * pkg.referralBps) / 10_000;
    }
}

contracts/SecondaryMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

interface IPancakeRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);

    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);

    function factory() external pure returns (address);
}

interface IPancakeFactory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

/**
 * @title SecondaryMarket
 * @dev Provides bidirectional USDT↔BLOCKS token swapping functionality
 * @notice This contract enables users to swap between USDT and BLOCKS tokens
 *         using PancakeSwap DEX with configurable target pricing and fees
 */
contract SecondaryMarket is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = DEFAULT_ADMIN_ROLE;
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // Token contracts
    IERC20 public immutable usdtToken;
    IERC20 public immutable token; // BLOCKS token

    // DEX integration
    IPancakeRouter public immutable router;
    IPancakeFactory public immutable factory;

    // Fee and pricing
    address public feeRecipient;
    uint256 public targetPrice; // Target price in USDT per BLOCKS (18 decimals)
    uint256 public swapFee; // Fee in basis points (e.g., 100 = 1%)

    // Constants
    uint256 private constant MAX_FEE = 1000; // 10% max fee
    uint256 private constant FEE_DENOMINATOR = 10000;

    // Events
    event TokensSwapped(
        address indexed user,
        uint256 blocksAmount,
        uint256 usdtAmount
    );

    event TargetPriceUpdated(uint256 oldPrice, uint256 newPrice);
    event SwapFeeUpdated(uint256 oldFee, uint256 newFee);
    event FeeRecipientUpdated(address oldRecipient, address newRecipient);

    /**
     * @dev Constructor
     * @param _usdtToken Address of the USDT token contract
     * @param _blocksToken Address of the BLOCKS token contract
     * @param _router Address of the PancakeSwap router
     * @param _factory Address of the PancakeSwap factory
     * @param _feeRecipient Address to receive swap fees
     * @param admin Address of the admin
     * @param _targetPrice Initial target price (USDT per BLOCKS, 18 decimals)
     */
    constructor(
        address _usdtToken,
        address _blocksToken,
        address _router,
        address _factory,
        address _feeRecipient,
        address admin,
        uint256 _targetPrice
    ) {
        require(_usdtToken != address(0), "SecondaryMarket: Invalid USDT token address");
        require(_blocksToken != address(0), "SecondaryMarket: Invalid BLOCKS token address");
        require(_router != address(0), "SecondaryMarket: Invalid router address");
        require(_factory != address(0), "SecondaryMarket: Invalid factory address");
        require(_feeRecipient != address(0), "SecondaryMarket: Invalid fee recipient address");
        require(admin != address(0), "SecondaryMarket: Invalid admin address");
        require(_targetPrice > 0, "SecondaryMarket: Invalid target price");

        usdtToken = IERC20(_usdtToken);
        token = IERC20(_blocksToken);
        router = IPancakeRouter(_router);
        factory = IPancakeFactory(_factory);
        feeRecipient = _feeRecipient;
        targetPrice = _targetPrice;
        swapFee = 100; // 1% default fee

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(OPERATOR_ROLE, admin);
    }

    /**
     * @dev Swap USDT for BLOCKS tokens
     * @param usdtAmount Amount of USDT to swap
     * @param minBlocksAmount Minimum amount of BLOCKS to receive
     */
    function swapUSDTForBLOCKS(uint256 usdtAmount, uint256 minBlocksAmount) 
        external 
        whenNotPaused 
        nonReentrant 
    {
        require(usdtAmount > 0, "SecondaryMarket: Invalid USDT amount");
        require(minBlocksAmount > 0, "SecondaryMarket: Invalid minimum BLOCKS amount");

        // Transfer USDT from user
        usdtToken.safeTransferFrom(msg.sender, address(this), usdtAmount);

        // Calculate fee
        uint256 feeAmount = (usdtAmount * swapFee) / FEE_DENOMINATOR;
        uint256 swapAmount = usdtAmount - feeAmount;

        // Transfer fee to fee recipient
        if (feeAmount > 0) {
            usdtToken.safeTransfer(feeRecipient, feeAmount);
        }

        // Perform swap via DEX
        address[] memory path = new address[](2);
        path[0] = address(usdtToken);
        path[1] = address(token);

        // Approve router to spend USDT
        IERC20(address(usdtToken)).forceApprove(address(router), swapAmount);

        // Execute swap
        uint[] memory amounts = router.swapExactTokensForTokens(
            swapAmount,
            minBlocksAmount,
            path,
            msg.sender,
            block.timestamp + 300 // 5 minute deadline
        );

        emit TokensSwapped(msg.sender, amounts[1], usdtAmount);
    }

    /**
     * @dev Swap BLOCKS for USDT tokens
     * @param blocksAmount Amount of BLOCKS to swap
     * @param minUsdtAmount Minimum amount of USDT to receive
     */
    function swapBLOCKSForUSDT(uint256 blocksAmount, uint256 minUsdtAmount) 
        external 
        whenNotPaused 
        nonReentrant 
    {
        require(blocksAmount > 0, "SecondaryMarket: Invalid BLOCKS amount");
        require(minUsdtAmount > 0, "SecondaryMarket: Invalid minimum USDT amount");

        // Transfer BLOCKS from user
        token.safeTransferFrom(msg.sender, address(this), blocksAmount);

        // Perform swap via DEX
        address[] memory path = new address[](2);
        path[0] = address(token);
        path[1] = address(usdtToken);

        // Approve router to spend BLOCKS
        IERC20(address(token)).forceApprove(address(router), blocksAmount);

        // Execute swap
        uint[] memory amounts = router.swapExactTokensForTokens(
            blocksAmount,
            minUsdtAmount,
            path,
            address(this),
            block.timestamp + 300 // 5 minute deadline
        );

        // Calculate fee on received USDT
        uint256 receivedUsdt = amounts[1];
        uint256 feeAmount = (receivedUsdt * swapFee) / FEE_DENOMINATOR;
        uint256 userAmount = receivedUsdt - feeAmount;

        // Transfer fee to fee recipient
        if (feeAmount > 0) {
            usdtToken.safeTransfer(feeRecipient, feeAmount);
        }

        // Transfer remaining USDT to user
        usdtToken.safeTransfer(msg.sender, userAmount);

        emit TokensSwapped(msg.sender, blocksAmount, userAmount);
    }

    /**
     * @dev Get swap quote for BLOCKS to USDT
     * @param blocksAmount Amount of BLOCKS to quote
     * @return usdtAmount Estimated USDT amount (after fees)
     */
    function getSwapQuote(uint256 blocksAmount) external view returns (uint256 usdtAmount) {
        if (blocksAmount == 0) return 0;

        address[] memory path = new address[](2);
        path[0] = address(token);
        path[1] = address(usdtToken);

        try router.getAmountsOut(blocksAmount, path) returns (uint[] memory amounts) {
            uint256 grossUsdt = amounts[1];
            uint256 feeAmount = (grossUsdt * swapFee) / FEE_DENOMINATOR;
            usdtAmount = grossUsdt - feeAmount;
        } catch {
            // Fallback to target price if DEX quote fails
            uint256 grossUsdt = (blocksAmount * targetPrice) / 1e18;
            uint256 feeAmount = (grossUsdt * swapFee) / FEE_DENOMINATOR;
            usdtAmount = grossUsdt - feeAmount;
        }
    }

    /**
     * @dev Update target price (admin only)
     * @param newTargetPrice New target price (USDT per BLOCKS, 18 decimals)
     */
    function updateTargetPrice(uint256 newTargetPrice) external onlyRole(ADMIN_ROLE) {
        require(newTargetPrice > 0, "SecondaryMarket: Invalid target price");
        uint256 oldPrice = targetPrice;
        targetPrice = newTargetPrice;
        emit TargetPriceUpdated(oldPrice, newTargetPrice);
    }

    /**
     * @dev Update swap fee (admin only)
     * @param newSwapFee New swap fee in basis points
     */
    function updateSwapFee(uint256 newSwapFee) external onlyRole(ADMIN_ROLE) {
        require(newSwapFee <= MAX_FEE, "SecondaryMarket: Fee too high");
        uint256 oldFee = swapFee;
        swapFee = newSwapFee;
        emit SwapFeeUpdated(oldFee, newSwapFee);
    }

    /**
     * @dev Update fee recipient (admin only)
     * @param newFeeRecipient New fee recipient address
     */
    function updateFeeRecipient(address newFeeRecipient) external onlyRole(ADMIN_ROLE) {
        require(newFeeRecipient != address(0), "SecondaryMarket: Invalid fee recipient");
        address oldRecipient = feeRecipient;
        feeRecipient = newFeeRecipient;
        emit FeeRecipientUpdated(oldRecipient, newFeeRecipient);
    }

    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Emergency function to withdraw stuck tokens (admin only)
     * @param tokenAddress Address of the token to withdraw
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address tokenAddress, uint256 amount) external onlyRole(ADMIN_ROLE) {
        IERC20(tokenAddress).safeTransfer(msg.sender, amount);
    }
}

